import React, { useState } from 'react';
import Tesseract from 'tesseract.js';

const BotAvatar = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [loading, setLoading] = useState(false);

  const handleBotClick = () => {
    document.getElementById('fileInput').click();
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      setLoading(true);

      // Use Tesseract.js to recognize text from the image
      Tesseract.recognize(file, 'eng', {
        logger: (m) => console.log(m),
      })
        .then(({ data: { text } }) => {
          setExtractedText(text);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error recognizing text:', error);
          setLoading(false);
        });
    }
  };

  const handleReadAloud = (language) => {
    if ('speechSynthesis' in window) {
      const speech = new SpeechSynthesisUtterance(extractedText);
      speech.lang = language; // Set language for speech synthesis
      window.speechSynthesis.speak(speech);
    } else {
      alert('Sorry, your browser does not support text-to-speech.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <div className="text-center">
        {/* Bot Avatar */}
        <img
          src="https://via.placeholder.com/150"
          alt="Bot Avatar"
          className="cursor-pointer w-40 h-40 rounded-full border-4 border-blue-500"
          onClick={handleBotClick}
        />

        <p className="text-lg mt-4">Click on the bot to upload an image</p>

        {/* Hidden file input */}
        <input
          id="fileInput"
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="hidden"
        />

        {/* Show the selected image */}
        {selectedImage && (
          <div className="mt-4">
            <img
              src={selectedImage}
              alt="Uploaded"
              className="w-60 h-auto border-2 border-gray-300"
            />
            {loading ? (
              <p className="mt-2 text-blue-500">Processing image...</p>
            ) : (
              <p className="mt-2 text-green-500">Text extracted successfully!</p>
            )}
          </div>
        )}
      </div>

      {/* Show extracted text */}
      {extractedText && (
        <div className="mt-6 p-4 bg-white shadow rounded">
          <h3 className="text-lg font-bold">Extracted Text:</h3>
          <p className="text-gray-700 mt-2">{extractedText}</p>

          {/* Read Aloud Button in English */}
          <button
            onClick={() => handleReadAloud('en-US')}
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 focus:outline-none"
          >
            Read Aloud (English)
          </button>

          {/* Read Aloud Button in Hindi */}
          <button
            onClick={() => handleReadAloud('hi-IN')}
            className="mt-4 ml-2 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 focus:outline-none"
          >
            Read Aloud (Hindi)
          </button>
        </div>
      )}
    </div>
  );
};

export default BotAvatar;
